import { getUserBalance } from "./Database";
import { useState } from "react";

export default class User {
    constructor(id, firstname, lastname) {
        this.id = id;
        this.firstname = firstname;
        this.lastname = lastname;
    }

    getBalance() {
        this.setBalance(getUserBalance());
    }

    getName(isFirstname) {
        if(isFirstname) return this.firstname;
        return this.lastname;
    }

    setFirstname(name) {
        this.firstname = name;
        this.updater();
    }

    setLastname(name) {
        this.lastname = name;
        this.updater();
    }

    setBalance(balance) {
        this.balance = balance;
        this.updater();
    }

    toString() {
        return `${this.firstname} ${this.lastname}${this.balance?` (${this.balance}€)`:``}`;
    }

    log() {
        console.log(this.toString());
    }
  
    setUpdater(func) {
        this.updater = func;
    }

    clone() {
        return new User(this.id, this.firstname, this.lastname, this.balance);
    }
}
