import React, {useState, useEffect} from 'react';
import { getProducts } from './Database';

ProductSelect.propTypes = {

}

export default function ProductSelect({resetCallback, runCallback, setResetCallback}) {
    // vars
    const [products, setProducts] = useState(getProducts);
    const [selectedProductId, setSelectedProductId] = useState(-1);

    // temp vars vor easier access
    const productSelected = selectedProductId!==-1;

    // set callback on beginning
    useEffect(() => {
        if(props.setResetCallback!=null)
            props.setResetCallback(prev => reset);
    }, []);

    function updateProduct(id) {
        // resets if product selection deleted
        if(id===-1)
            return reset();
           
        setSelectedProductId(id);
    }

    function reset() {
        setSelectedProductId(-1);
        if(props.reset)
            props.reset();
    }

    function submit() {
        // checks if product selected
        if(!productSelected) {
            console.log("Error: no valid entries");
            window.alert("Error: no valid entries");
            return;
        }

        if(props.run!=null)
            props.run(products.find(product => product.id===selectedProductId));

        if(props.resetSubmit)
            reset();
    }

    return(
        <div className="wrapper">
            <div className='wrapper'>{"Product: "}
            <select value={selectedProductId} onChange={(event) => {updateProduct(parseInt(event.target.value));}}>
                {<option value={-1}>{""}</option>}
                {products.map(({id, name, price}) => {
                    return <option key={id} value={id}>{`${name}(${+price.toFixed(2)}€)`}</option>
                })}
            </select></div>
            {props.useReset  && (!props.hideReset  || productSelected) &&   <button className='wrapper' onClick={reset}>{"reset"}</button>}
            {props.useSubmit && (!props.hideSubmit || productSelected) &&   <button className='wrapper' onClick={submit}>{"submit"}</button>}
        </div>
    );
}
