import React from 'react';

export default class Product extends React.Component {


    render() {
        scaleFontSize(this.props.id);
        return(
            <button id={this.props.id} className="product" onClick={() => this.props.setAmount(this.props.id, this.props.amount+(this.props.remove&&this.props.amount>0?-1:1))}>
                {this.props.name} <br />
                {this.props.price.toFixed(2)+'€'} <br />
                <div style={this.props.amount>0?{color: 'red'}:{}}>{this.props.amount>0 ? this.props.amount : 0}</div>
            </button>
        );
    }
}

function scaleFontSize(element) {
    console.log(element);
    var container = document.getElementById(element);

    container.style.fontSize = "100%";

    if(container.scrollWidth > container.clientWidth) {
        container.style.fontSize = "70%";
    }
}
