// import './App.css';
import React from 'react';

class Product extends React.Component {
    constructor(props) {
        super(props);
        // this.settings = {
        //     name: 'undefined',
        //     price: 0.00
        // }
        this.state = {
            amount: 0
        };
    }

    render() {
        return(
            <button className="Product" onClick={() => this.add()}>
                <ol>{this.props.name}</ol>
                <ol>{this.props.price.toFixed(2)+'€'}</ol>
                <ol style={{color: 'red'}}>{this.state.amount>0 ? this.props.amount : null}</ol>
            </button>
        );
    }

    add() {
        this.props.setAmount(1);
        this.setState({
            amount: this.state.amount+1
        });
    }

    reset() {
        this.setState({
            amount: 0
        });
    }
}

class BuyUi extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            products: [
                { name: "Bonbon", price: .05, amount: 0},
                { name: "Schokoriegel", price: .7, amount: 0},
                { name: "Loli", price: .5, amount: 0},
                { name: "Kracher", price: .1, amount: 0}
            ]
        }
    }
    
    render() {
        return(
            <div className="BuyUi">
                {this.state.products.map(({name, price, amount}) => {return <Product key={name} name={name} price={price} amount={amount} setAmount={(a) => {this.state.products.find(name).amount = a;}}/>})}
                <button className="submit" onClick={() => this.submit()}>{"kaufen"}</button>
            </div>
        );
    }


}

export default BuyUi;
