// import '../App.css';
import React from 'react';
import ProductSelect from './ProductSelect';

export default class ChangeProduct extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            product: null,
        };
    }

    setProduct(product) {
        console.log(product);
        this.setState({product: product});
    }
    
    reset() {
        this.setState({
            productName: "",
            productPrice: 0,
        });
    }

    submit() {
        console.log(this.state.productName);
        console.log(this.state.productPrice);
        this.reset();
        // do server
    }

    render() {
        return(
            <div className='ChangeProduct'>
                <p>{"Change Product"}</p>
                <ProductSelect run={this.setProduct.bind(this)} />
                {"Name: "} <input type="text" value={this.state.product.name} onChange={event=>{this.setState({productName: event.target.value})}} />
                {"Price: "} <input type="number" value={this.state.product==null?null:this.state.product.price.toString()} onChange={event=>{this.setState({productPrice: Math.floor(parseFloat(event.target.value)*100)/100})}} onKeyPress={(event)=>{if(!/[0-9|.]/.test(event.key)) event.preventDefault();}} />
                <button className='reset' onClick={this.reset.bind(this)}>{"reset"}</button>
                <button className='submit' onClick={this.submit.bind(this)}>{"submit"}</button>
            </div>
        );
    }
}
