import React, { useState, useEffect } from 'react';
import UserSelect from '../util/UserSelect';
import NumberInput from '../util/NumberInput';
import { changeUser, getUserBalance } from '../util/Database';
import User from '../util/User';
import { useForceUpdate } from '../util/Util';

export default function ChangeUser(props) {
    // vars
    const [user, setUser] = useState(null);
    const [newUser, setNewUser] = useState(null);

    const [resetCallback, setResetCallback] = useState();

    const forceUpdate = useForceUpdate();

    useEffect(() => {
        if(!user) return;
        user.setUpdater(forceUpdate);
        user.getBalance()
        if(user.balance === null) return;
        user.log();
        setNewUser(user.clone());
    }, [user]);

    useEffect(() => {
        if(!newUser) return;
        newUser.setUpdater(forceUpdate);
        newUser.getBalance();
    }, [newUser])

    function resetAll() {
        setUser(null);
        setNewUser(null);
    }
    
    function reset() {
        setNewUser(user);
    }

    function submit() {
        if(user==newUser) {
            console.log("nothing changed");
            window.alert("Nothing changed");
            return;
        }
        if(newUser.firstname==="" || newUser.lastname==="" || newUser.balance==null) {
            console.log("Error no valid entries");
            window.alert("Error: No valid entries");
            return;
        }
        if(window.confirm(`Change ${user.toString()} to ${newUser.toString()} ?`)) {
            console.log(`Change ${user.toString()} to ${newUser.toString()}`);
            changeUser(user, newUser);

            if(resetCallback)
                resetCallback();
        }
    }

    function changeUserUi() {
        return(
            <div>
                <div className='wrapper'>{"Vorname: "} <input value={newUser.firstname} onChange={event => newUser.setFirstname(event.target.value)} /></div>
                <div className='wrapper'>{"Nachname: "} <input value={newUser.lastname} onChange={event => newUser.setLastname(event.target.value)} /></div>
                <div className='wrapper'>{"Kontostand: "} <NumberInput value={newUser.balance} setValue={(value) => newUser.setBalance(value)} /></div>
                {newUser!==user && <button className='wrapper' onClick={reset}>{"reset"}</button>}
                {<button className='wrapper' onClick={submit}>{"submit"}</button>}
            </div>
        );
    }

    return(
        <div className='rubric'>
            <div className='title'>{"Change User"}</div>
            <UserSelect runCallback={setUser} resetCallback={resetAll} setResetCallback={setResetCallback} useReset={true} hideReset={true}/>
            {newUser && changeUserUi()}
        </div>
    );
}
