import React, {useState, useEffect} from 'react';
import { getUsers } from './Database';

const defaultTitle = "Suchen";

export default function UserSelect({title=defaultTitle, runCallback, resetCallback, setResetCallback, useReset, hideReset, useSubmit, hideSubmit, resetOnSubmit}) {

    const [users, setUsers] = useState(getUsers());
    const [userFirstname, setUserFirstname] = useState("");
    const [userLastname, setUserLastname] = useState("");

    const filteredUsers = getFilteredUsers();

    useEffect(() => {
      if(setResetCallback!=null)
        setResetCallback(()=>reset);
    }, []);

    useEffect(() => {
        if(userFirstname!=="" && userLastname!=="")
            run();
        else
            checkOptions();
    }, [userFirstname, userLastname]);


    function updateName(isFirstname, name) {
        if(name === "")
            return reset();

        if(isFirstname)
            setUserFirstname(name);
        else
            setUserLastname(name);
    }

    function checkOptions() {
        if(filteredUsers.length > 1)
            return;

        if(userFirstname!=="" && userLastname==="")
            return setUserLastname(findUser(true, userFirstname).lastname);
        if(userFirstname==="" && userLastname!=="")
            return setUserFirstname(findUser(false, userLastname).firstname);
    }

    function getFilteredUsers() {
        return users.filter(({lastname, firstname}) => (userFirstname==="" || userFirstname===firstname) && (userLastname==="" || userLastname===lastname));
    }

    function getUniqueUsers(isFirstname) {
        return filteredUsers.filter(({id, firstname, lastname}) => findUser(isFirstname, isFirstname?firstname:lastname).id===id).sort((user1, user2) => compareNames(isFirstname?user1.firstname:user1.lastname, isFirstname?user2.firstname:user2.lastname))
    }

    function findUser(isFirstname, name) {
        return filteredUsers.find(({firstname, lastname}) => (isFirstname?firstname:lastname===name));
    }

    function compareNames(name1, name2) {
        return name1.toLowerCase().localeCompare(name2.toLowerCase());
    }

    function run() {
        console.log(userFirstname+" "+userLastname);
        if(!useSubmit)
            submit();
    }

    function submit() {
        if(getFilteredUsers().length!==1) {
            console.log("Error: user selection ambiguous");
            window.alert("Error: user selection ambiguous");
            return;
        }

        if(runCallback!=null)
            runCallback(getFilteredUsers()[0]);

        if(resetOnSubmit)
            reset();
    }

    function reset() {
        setUserFirstname("");
        setUserLastname("");
        if(resetCallback!=null)
            resetCallback();
    }

    function nameSelectUi(isFirstname) {
        let userName = isFirstname?userFirstname:userLastname;
        return(
            <select value={userName} onChange={event => updateName(isFirstname, event.target.value)}>
                <option value="">{userName!==""?"delete":""}</option>
                {getFilteredUsers(isFirstname).map(({id, firstname, lastname}) => {let name=isFirstname?firstname:lastname; return <option key={id} value={name}>{name}</option>})}
            </select>
        );
    }
    
    return(
        <div className="rubric">
            <div className='title'>{title}</div>
            <div className='wrapper'>
                {"Vorname "}{nameSelectUi(true)}
            </div>
            <div className='wrapper'>
                {"Nachname "}{nameSelectUi(false)}
            </div>
            {useReset  && (!hideReset  || userFirstname!=="" || userLastname!=="") &&   <button className='wrapper' onClick={reset}>{"reset"}</button>}
            {useSubmit && (!hideSubmit || filteredUsers.length===1) &&                  <button className='wrapper' onClick={submit}>{"submit"}</button>}
        </div>
    );
}
