// import '../App.css';
import React from 'react';
import ProductSelect from './ProductSelect';
import UserSelect from './UserSelect';

export default class GeneralUi extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            user: null,
            userBalance: null,

            resetCallbac: ()=>{},
            submitCallbac: ()=>{},
        }
    }

    setUser(user) {
        console.log(user);
        this.setState({user: user})
        this.getUserBalance();
    }
    
    setResetCallback(func) {
        this.setState({resetCallbac: func});
    }

    setSubmitCallback(func) {
        this.setState({submitCallbac: func});
    }

    getUserBalance() {
        // do server
        let balance = 10;
        this.setState({userBalance: balance});
    }

    reset() {
        this.setState({user: null, userBalance: null});
    }

    submit() {
        this.state.submitCallbac();
        // this.reset();
        this.state.resetCallbac();
    }

    render() {
        return(
            <div className="GeneralUi">
                <UserSelect setResetCallback={this.setResetCallback.bind(this)} reset={this.reset.bind(this)} setUser={this.setUser.bind(this)} useSubmit={false} />
                <ProductSelect setSubmitCallback={this.setSubmitCallback.bind(this)} userBalance={this.state.userBalance} />
                <button className="submit" onClick={this.submit.bind(this)}>{"kaufen"}</button>
            </div>
        );
    }
}
