// import '../App.css';
import React from 'react';
import ProductSelect from './ProductSelect';

export default class ChangeProduct extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            product: null,
            newName: null,
            newPrice: null,
        };
    }

    setProduct(product) {
        console.log(product);
        this.setState({
            product: product,
            newProduct: product,
        });
    }

    resetAll() {
        this.setState({
            product: null,
            newProduct: null,
        });
    }
    
    reset() {
        this.setState({newProduct: this.state.product});
    }

    submit() {
        console.log(this.state.productName);
        console.log(this.state.productPrice);
        this.reset();
        // do server
    }

    updateProductAmount = (id, amount) => {
        const product = this.state.products.find(product => product.id === id);
        if (product) {
            const updatedProduct = { ...product, amount };
            const updatedProducts = [];
            this.state.products.map((product) => {updatedProducts.push(product.id==id?updatedProduct:product);})
            this.setState({ products: updatedProducts });
        }
    }

    changeProductUi() {
        return(
            <div>
                {"Name: "} <input type="text" value={this.state.newProduct.name} onChange={event=>{this.setState({newProduct: {...this.state.newProduct, name: event.target.value}})}} />
                {"Price: "} <input type="number" value={this.state.newProduct.price.toString()} onChange={event=>{this.setState({newProduct: {...this.state.newProduct, price: Math.floor(parseFloat(event.target.value)*100)/100}})}} onKeyPress={(event)=>{if(!/[0-9|.]/.test(event.key)) event.preventDefault();}} />
                <button className='reset' onClick={this.reset.bind(this)}>{"reset"}</button>
                <button className='submit' onClick={this.submit.bind(this)}>{"submit"}</button>
            </div>
        );
    }

    render() {
        return(
            <div className='ChangeProduct'>
                <p>{"Change Product"}</p>
                <ProductSelect run={this.setProduct.bind(this)} reset={this.resetAll.bind(this)} />
                {this.state.product!=null?this.changeProductUi():null}
            </div>
        );
    }
}
