// import '../App.css';
import React from 'react';
import ProductSelect from '../adminUi/ProductSelect';

export default class RemoveProduct extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            selectedProduct: {id: null, name: "", price: null},
        }
    }

    setProduct(product) {
        this.setState({selectedProduct: product});
    }

    render() {
        return(
            <div className='RemoveProduct'>
                <p>{"Remove Product"}</p>
                <ProductSelect setProduct={this.setProduct.bind(this)} />
            </div>
        );
    }
}
