import { getUserBalance } from "./Database";

export default class User {
    constructor(id, firstname, lastname) {
        this.id = id;
        this.firstname = firstname;
        this.lastname = lastname;
    }

    getBalance() {
        this.balance = getUserBalance();
    }

    getName(isFirstname) {
        if(isFirstname) return this.firstname;
        return this.lastname;
    }

    setFirstname(name) {
        this.firstname = name;
    }

    setLastname(name) {
        this.lastname = name;
    }

    setBalance(balance) {
        console.log(balance);
        this.balance = balance;
    }

    toString() {
        return `${this.firstname} ${this.lastname}${this.balance?` (${this.balance}€)`:``}`;
    }

    log() {
        console.log(this.toString);
    }

    setUpdater(func) {
        this.updater = func;
    }
}
