import React, {useState, useEffect} from 'react';
import {Link} from "react-router-dom";
import ProductDisplay from './ProductDisplay';
import UserSelect from '../util/UserSelect';
import BalanceInfos from './BalanceInfos';
import BalanceCorrection from './BalanceCorrection';
import CashPayment from './ChashPayment';
import LastBookings from './LastBookings';

export default function GeneralUi(props) {
    const [user, setUser] = useState();
    const [userBalance, setUserBalance] = useState();

    const [correctionPlus, setCorrectionPlus] = useState();
    const [correctionMinus, setCorrectionMinus] = useState();
    const [paymentIn, setPaymentIn] = useState();
    const [paymentOut, setPaymentOut] = useState();

    const [products, setProducts] = useState(getProducts());
    
    const [resetCallback, setResetCallback] = useState();
    const [resetProductsCallback, setresetProductsCallback] = useState();

    
    useEffect(() => { getUserBalance(user) }, [user]);

    function getProducts() {
        // do sever
        return [
            { id: 0, name: "Bonbon", price: .05, amount: 0,},
            { id: 1, name: "Schokoriegel", price: .7, amount: 0,},
            { id: 2, name: "Loli", price: .5, amount: 0,},
            { id: 3, name: "Kracher", price: .1, amount: 0,},
            { id: 4, name: "Slush", price: .2, amount: 0,},
            { id: 5, name: "M&M", price: .3, amount: 0,},
            { id: 6, name: "Schlangen", price: .1, amount: 0,},
            { id: 7, name: "Snickers", price: .6, amount: 0,},
            { id: 8, name: "T-shirt", price: 15, amount: 0,},
            { id: 9, name: "Cappy", price: 8, amount: 0,},
            { id: 10, name: "CD", price: 10, amount: 0,},
            { id: 11, name: "Bibel", price: 20, amount: 0,},
            { id: 12, name: "Sprudel", price: 0.8, amount: 0,},
            { id: 13, name: "Apfelschorle", price: 1, amount: 0,},
            { id: 14, name: "Cola", price: 1.5, amount: 0,},
            { id: 15, name: "Reis", price: 2, amount: 0},
            { id: 16, name: "ESP", price: 5, amount: 0},
        ];
    }

    function getUserBalance(user) {
        // do server
        let balance = 10;
        setUserBalance(balance);
    }

    function calculateTotal() {
        var price = 0;
        for(var i = 0, l = products.length; i < l; i++)
            price+=products[i].amount*products[i].price;
        price-=correctionPlus;
        price+=correctionMinus;
        price-=paymentIn;
        price+=paymentOut;
        return price;
    }

    function resetUser() {
        setUser(null);
        setUserBalance(null);
    }

    function resetProducts() {
        const updatedProducts = [];
        products.map(product => {
            const updatedProduct = {...product, amount: 0 };
            updatedProducts.push(updatedProduct);
        });
        setProducts(updatedProducts);
        setCorrectionPlus(null);
        setCorrectionMinus(null);
        setPaymentIn(null);
        setPaymentOut(null);
    }

    function submit() {
        console.log(calculateTotal());
        // do server
        
        if(resetCallback!=null)
            resetCallback();
        resetProducts();
    }

    return(
        <div className="main">
            {/* <Link to="/admin">{"Admin"}</Link> */}
            <UserSelect setResetCallback={func=>setResetCallback(func)} reset={resetUser} run={setUser} useSubmit={false} useReset={true} hideReset={true} />
            <ProductDisplay remove={calculateTotal()>userBalance} products={products} setProducts={products=>setProducts(products)} />
            <BalanceCorrection plus={correctionPlus} setPlus={value=>setCorrectionPlus(value)} minus={correctionMinus} setMinus={value=>setCorrectionMinus(value)} />
            <CashPayment out={paymentOut} setOut={value=>setPaymentOut(value)} in={paymentIn} setIn={value=>setPaymentIn(value)} /><br className='wrapper'/>
            {user!=null&&<BalanceInfos balance={userBalance} sum={calculateTotal()} />}
            {user!=null&&<LastBookings />}
            {(calculateTotal()!==0||correctionPlus!=null||paymentIn!=null)&&<button className='wrapper' onClick={resetProducts.bind(this)}>{"reset"}</button>}
            {calculateTotal()<=userBalance&&(calculateTotal()!==0||correctionPlus!==null||paymentIn!=null)&&user!=null&&<button className="wrapper" onClick={submit.bind(this)}>{"Buchen"}</button>}
        </div>
    );
}
