// import '../App.css';
import React from 'react';

export default class UserSelect extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            users: [
                { id: "0", name: "Mauch", vorname: "Josua" },
                { id: "1", name: "Tappe", vorname: "Isajah" },
                { id: "2", name: "Braun", vorname: "Jonas" },
                { id: "3", name: "Strasser", vorname: "Marit" },
                { id: "4", name: "Pauli", vorname: "Lotta" },
                { id: "5", name: "Volmer", vorname: "Hannah" },
                { id: "6", name: "Schwarz", vorname: "Tim" },
                { id: "7", name: "Schreiber", vorname: "Jan" },
                { id: "8", name: "Günther", vorname: "William" },
                { id: "9", name: "Mustermann", vorname: "Tim"},
            ],
        }
    }

    updateName(isVorname, name) {
        if(isVorname)
            this.setState({userVorName: name});
        else
            this.setState({userName: name});
        console.log(this.state.userVorName + " " + this.state.userName);
    }

    render() {
        return(
            <div className="UserSelect">
                <select className="vorname" onChange={this.updateName(true)}>
                    <option value=""></option>
                    {this.state.users.map(({id, name, vorname}) => { 
                        if(this.state.users.findIndex(object => {return object.vorname === vorname}) === this.state.users.findIndex(object => {return object.id === id}))
                            return <option key={id} value={name}>{vorname}</option>
                    })}
                </select>
                <select className="name" onChange={this.updateName(false)}>
                    {this.state.users.map(({id, name, vorname}) => { 
                        return <option key={id} value={name}>{name}</option>
                    })}
                </select>
            </div>
        );
    }
}
