// import './App.css';
import React from 'react';



class BuyUi extends React.Component {
    constructor(props) {
        super(props);
    }
    
    submit() {
        
    }

    render() {
        return(
            <div className="BuyUi">
                
                <button className="submit" onCLick={() => this.submit()}>{"kaufen"}</button>
            </div>
        );
    }


}

export default BuyUi;

class Product extends React.Component {
    constructor(props) {
        super(props);
        // this.settings = {
        //     name: 'undefined',
        //     price: 0.00
        // }
        this.state = {
            amount: 0
        }
    }

    render() {
        return(
            <button className="Product" onClick={() => this.add()}>
                <ol>{this.props.name}</ol>
                <ol>{this.props.price.toFixed(2)+'€'}</ol>
                <ol style={{color: 'red'}}>{this.state.amount>0 ? this.state.amount : null}</ol>
            </button>
        );
    }

    add() {
        this.setState({
            amount: this.state.amount+1
        });
    }

    reset() {
        this.setState({
            amount: 0
        });
    }
}
