// import '../App.css';
import React from 'react';
import ProductSelect from './ProductSelect';
import UserSelect from './UserSelect';

export default class GeneralUi extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            userId: null,
            userFirstname: "",
            userLastname: "",
            userBalance: null,
        }
    }

    setUserId(id) {
        this.setState({userId: id});
        console.log(id);
        this.getUserBalance();
    }

    setUserFirstname(name, callback) {
        this.setState({userFirstname: name}, callback);
    }

    setUserLastname(name, callback) {
        this.setState({userLastname: name}, callback);
    }

    getUserBalance() {
        let balance = 10;
        this.setState({userBalance: balance});
    }

    render() {
        return(
            <div className="GeneralUi">
                <UserSelect setUserId={this.setUserId.bind(this)} setUserFirstname={this.setUserFirstname.bind(this)} setUserLastname={this.setUserLastname.bind(this)} userFirstname={this.state.userFirstname} userLastname={this.state.userLastname} />
                <ProductSelect userBalance={this.state.userBalance} />
            </div>
        );
    }
}
