// import '../App.css';
import React from 'react';

export default class AddProduct extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            productName: "",
            productPrice: null,
        };
    }

    reset() {
        this.setState({
            productName: "",
            productPrice: null,
        });
    }

    submit() {
        if(this.state.productName === "" || this.state.productPrice === null) {
            console.log("Error no valid entries");
            return;
        }
        console.log(this.state);
        // do server

        this.reset();
    }

    render() {
        return(
            <div className='AddProduct'>
                <p>{"Add Product"}</p>
                {"Name: "} <input type="text" value={this.state.productName} onChange={event=>{this.setState({productName: event.target.value})}} />
                {"Price: "} <input type="number" value={this.state.productPrice===null?"":this.state.productPrice.toString()} onChange={event=>{this.setState({productPrice: event.target.value===""?null:Math.floor(parseFloat(event.target.value)*100)/100})}} onKeyPress={(event)=>{if(!/[0-9|.]/.test(event.key)) event.preventDefault();}} />
                <button className='reset' onClick={this.reset.bind(this)}>{"reset"}</button>
                <button className='submit' onClick={this.submit.bind(this)}>{"submit"}</button>
            </div>
        );
    }
}
