// import '../App.css';
import React from 'react';

export default class UserSelect extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            users: [
                { id: "0", lastname: "Mauch", vorname: "Josua" },
                { id: "1", lastname: "Tappe", vorname: "Isajah" },
                { id: "2", lastname: "Braun", vorname: "Jonas" },
                { id: "3", lastname: "Strasser", vorname: "Marit" },
                { id: "4", lastname: "Pauli", vorname: "Lotta" },
                { id: "5", lastname: "Volmer", vorname: "Hannah" },
                { id: "6", lastname: "Schwarz", vorname: "Tim" },
                { id: "7", lastname: "Schreiber", vorname: "Jan" },
                { id: "8", lastname: "GÃ¼nther", vorname: "William" },
                { id: "9", lastname: "Mustermann", vorname: "Tim"},
                { id: "10", lastname: "Mustermann", vorname: "Fridolin"},
            ],
            userVorname: "",
            userLastname: "",
        }
    }

    updateName(isVorname, name) {
        console.log(name);
        if(isVorname)
            this.setState({userVorname: name});
        else
            this.setState({userLastname: name});
            if(isVorname && name!=="" && this.state.userLastname!=="" && this.getFilteredUsers().length <= 1)
                this.setState({userLastname: this.getFilteredUsers().find(user => {return user.vorname === name}).lastname});
            else if(isVorname && name!=="" && this.state.userVorname!=="" && this.getFilteredUsers().length <= 1)
                this.setState({userVorname: this.getFilteredUsers().find(user => {return user.lastname === name}).vorname});
        console.log(this.state.userVorname+"_"+this.state.userLastname);
    }

    getFilteredUsers() {
        return this.state.users.filter(({id, lastname, vorname}) => {return (this.state.userVorname === "" || this.state.userVorname === vorname) && (this.state.userLastname === "" || this.state.userLastname === lastname)});
    }

    render() {
        const users = this.getFilteredUsers();
        return(
            <div className="UserSelect">
                <select className="vorname" onChange={(event) => {this.updateName(true, event.target.value);}}>
                    {<option value=""></option>}
                    {users.map(({id, lastname, vorname}) => { 
                        if(users.findIndex(object => {return object.vorname === vorname}) === users.findIndex(object => {return object.id === id}))
                            return <option key={id} value={vorname}>{vorname}</option>
                    })}
                </select>
                <select className="lastname" onChange={(event) => {this.updateName(false, event.target.value);}}>
                    {/* {users.length>1?<option value=""></option>:null} */}
                    {<option value=""></option>}
                    {users.map(({id, lastname, vorname}) => {
                        if(users.findIndex(object => {return object.lastname === lastname}) === users.findIndex(object => {return object.id === id}))
                            return <option key={id} value={lastname}>{lastname}</option>
                    })}
                </select>
            </div>
        );
    }
}
