// import './App.css';
import React from 'react';

class Product extends React.Component {

    render() {
        return(
            <button className="Product" onClick={() => this.add()}>
                <ol>{this.props.name}</ol>
                <ol>{this.props.price.toFixed(2)+'€'}</ol>
                <ol style={{color: 'red'}}>{this.props.amount>0 ? this.props.amount : null}</ol>
            </button>
        );
    }

    add() {
        this.props.setAmount(this.props.name, this.props.amount+1);
        // this.setState({
        //     amount: this.state.amount+1
        // });
    }
}

class BuyUi extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            products: [
                { name: "Bonbon", price: .05, amount: 0,},
                { name: "Schokoriegel", price: .7, amount: 0,},
                { name: "Loli", price: .5, amount: 0,},
                { name: "Kracher", price: .1, amount: 0,}
            ]
        }
    }

    updateProductAmount = (name, amount) => {
        const product = this.state.products.find(product => product.name === name);
        if (product) {
          const updatedProduct = { ...product, amount };
          const updatedProducts = this.state.products
            .filter(product => product.name !== name)
            .concat(updatedProduct);
          this.setState({ products: updatedProducts });
        }
      }
    
    render() {
        return(
            <div className="BuyUi">
                {this.state.products.map(({name, price, amount}) => { 
                    return <Product key={name} name={name} price={price} amount={amount} setAmount={this.updateProductAmount}/>
                })}
                <button className="submit" onClick={() => this.submit()}>{"kaufen"}</button>
            </div>
        );
    }


}

export default BuyUi;
