// import '../App.css';
import React from 'react';
import ProductSelect from './ProductSelect';
import UserSelect from './UserSelect';

export default class GeneralUi extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            user: null,
            userBalance: null,
        }
    }

    setUser(user) {
        console.log(user);
        this.setState({user: user})
    }

    getUserBalance() {
        // do server
        let balance = 10;
        this.setState({userBalance: balance});
    }

    reset() {
        this.setState({user: null, userBalance: null,})
    }

    render() {
        return(
            <div className="GeneralUi">
                <UserSelect reset={this.reset.bind(this)} setUser={this.setUser.bind(this)} />
                <ProductSelect setSubmitCallback={this.setSubmitCallback.bind(this)} userBalance={this.state.userBalance} />
                <button className="submit" onClick={this.submit}>{"kaufen"}</button>
            </div>
        );
    }
}
