// import '../App.css';
import React from 'react';
import UserSelect from '../generalUi/UserSelect';

export default class RemoveUser extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            user: null,
        }
    }

    setUser(user) {
        console.log(user);
        this.setState({user: user});

        // do server
    }

    reset() {
        this.setState({userId: null, userFirstname: "", userLastname: ""})
    }

    render() {
        return(
            <div className='RemoveUser'>
                <p>{"Remove User"}</p>
                {"Select User: "} <UserSelect reset={this.reset.bind(this)} setUser={this.setUser.bind(this)} useSubmit={true} />
            </div>
        );
    }
}
