import './App.css';
import React from 'react';

class BuyUi extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        return(
            <div className="BuyUi">
                <Product name = "Bonbon" price = {1.} />
            </div>
        );
    }

}

export default BuyUi;

class Product extends React.Component {
    constructor(props) {
        super(props);
        // this.settings = {
        //     name: 'undefined',
        //     price: 0.00
        // }
        this.state = {
            amount: 0
        }
    }

    render() {
        return(
            <button className="Product" onClick={() => this.add()}>
                <ol>{this.props.name}</ol>
                <ol>{this.props.price+'€'}</ol>
                <ol>{this.state.amount>0 ? this.state.amount : this.state.amount}</ol>
            </button>
        );
    }

    add() {
        this.setState({
            amount: this.state.amount+1,
        });
    }
}
