import React, {useState, useEffect} from 'react';
import { getUsers } from './Database';

export default function UserSelect({setResetCallback, runCallback, resetCallback, title, useReset, hideReset, useSubmit, hideSubmit, resetOnSubmit}) {

    const [users, setUsers] = useState(getUsers());
    const [userFirstname, setUserFirstname] = useState("");
    const [userLastname, setUserLastname] = useState("");

    const filteredUsers = getFilteredUsers();

    useEffect(() => {
      if(setResetCallback!=null)
        setResetCallback(()=>reset);
    }, []);

    useEffect(() => {
        if(userFirstname!=="" && userLastname!=="")
            run();
        else
            checkOptions();
    }, [userFirstname, userLastname]);


    function updateName(isFirstname, name) {
        if(name === "")
            return reset();

        if(isFirstname)
            setUserFirstname(name);
        else
            setUserLastname(name);
    }

    function checkOptions() {
        if(filteredUsers.length > 1)
            return;

        if(userFirstname!=="" && userLastname==="")
            return setUserLastname(filteredUsers.find(user => user.firstname===userFirstname).lastname);
        if(userFirstname==="" && userLastname!=="")
            return setUserFirstname(filteredUsers.find(user => user.lastname===userLastname).firstname);
    }

    function getFilteredUsers() {
        return users.filter(({lastname, firstname}) => (userFirstname==="" || userFirstname===firstname) && (userLastname==="" || userLastname===lastname));
    }

    function run() {
        console.log(userFirstname+" "+userLastname);
        if(!useSubmit)
            submit();
    }

    function submit() {
        if(getFilteredUsers().length!==1) {
            console.log("Error: user selection ambiguous");
            window.alert("Error: user selection ambiguous");
            return;
        }

        if(run!=null)
            run(getFilteredUsers()[0]);

        if(resetOnSubmit)
            reset();
    }

    function reset() {
        setUserFirstname("");
        setUserLastname("");
        if(resetCallback!=null)
            resetCallback();
    }
    
    return(
        <div className="rubric">
            <div className='title'>{title!=null?title:"Suchen"}</div>
            <div className='wrapper'>{"Vorname "}
                <select value={userFirstname} onChange={event => updateName(true, event.target.value)}>
                    <option value="">{userFirstname!==""?"delete":""}</option>
                    {filteredUsers.map(({id, firstname}) => { 
                        if(filteredUsers.find(user => user.firstname===firstname).id === id)
                            return <option key={id} value={firstname}>{firstname}</option>
                        return null;
                    })}
                </select>
            </div>
            <div className='wrapper'>{"Nachname "}
                <select value={userLastname} onChange={event => updateName(false, event.target.value)}>
                    <option value="">{userLastname!==""?"delete":""}</option>
                    {filteredUsers.map(({id, lastname}) => {
                        if(filteredUsers.find(user => user.lastname===lastname).id === id)
                            return <option key={id} value={lastname}>{lastname}</option>
                        return null;
                    })}
                </select>
            </div>
            {useReset  && (!hideReset  ||userFirstname!==""||userLastname!=="") &&  <button className='wrapper' onClick={reset}>{"reset"}</button>}
            {useSubmit && (!hideSubmit ||filteredUsers.length===1) &&               <button className='wrapper' onClick={submit}>{"submit"}</button>}
        </div>
    );
}
