    this.toString = function() {
        return `${this.firstname} ${this.lastname}${this.balance?` (${this.balance}€)`:``}`;
    }

    this.log = function() {
        console.log(this.toString());
    }
  
    this.setUpdater = function(func) {
        this.updater = func;
    }

    this.clone = function() {
        return new User(this.id, this.firstname, this.lastname, this.balance);
    }
}
